import React, { useState, useEffect } from "react";
import {
  Image,
  View,
  Text,
  FlatList,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
} from "react-native";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import MockData from "../data/data.json";

export default function Community({ navigation }) {
  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(MockData);
  }, []);

  const renderItem = ({ item }) => {
    return (
      <TouchableOpacity
        style={containers.ItemContainer}
        onPress={() => navigation.navigate("Item", { item })}
      >
        {/* <Image
          source={{
            uri: `${product.imgUrl}`,
          }}
          style={{ width: "100%", height: windowWidth * 0.4 }}
        /> */}

        <Text style={texts.user}>{item.user}</Text>
        <Text style={texts.title}>{item.title}</Text>
        <Text style={texts.content}>{item.content}</Text>

        <View style={{ flexDirection: "row" }}>
          {item.tags.map((tag, index) => {
            return (
              <TouchableOpacity key={index} style={buttons.tags}>
                <Text>{tag}</Text>
              </TouchableOpacity>
            );
          })}
        </View>

        <Text style={texts.comment}>댓글 {item.comment}개</Text>

        <View style={containers.likesContainer}>
          <TouchableOpacity style={[buttons.likes, { right: 90 }]}>
            <MaterialCommunityIcons
              name="heart-circle"
              size={18}
              color="black"
            />
            <Text>관심있어요 {item.marks}</Text>
          </TouchableOpacity>

          <TouchableOpacity style={buttons.likes}>
            <MaterialCommunityIcons name="hand-clap" size={18} color="black" />
            <Text>짝짝 {item.likes}</Text>
          </TouchableOpacity>
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <View style={containers.container}>
      <FlatList
        data={items}
        keyExtractor={(item) => item.id}
        renderItem={renderItem}
        showsVerticalScrollIndicator={false}
        initialNumToRender={20}
        maxToRenderPerBatch={20}
        windowSize={3}
        ListHeaderComponent={
          <View style={{ marginTop: 10 }}>
            <Text style={{ fontWeight: "600", fontSize: 15 }}>모든 글</Text>
          </View>
        }
        ListFooterComponent={<View style={{ marginBottom: 10 }}></View>}
      />
    </View>
  );
}

const containers = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
  },
  ItemContainer: {
    backgroundColor: "white",
    borderRadius: 18,
    marginVertical: 20,
    paddingHorizontal: 13,
    paddingVertical: 10,
    width: Dimensions.get("window").width * 0.93,
  },
  likesContainer: {
    flexDirection: "row",
    justifyContent: "flex-end",
    position: "relative",
  },
});

const texts = StyleSheet.create({
  user: { fontWeight: "600", marginBottom: 12 },
  title: {
    fontWeight: "600",
    fontSize: 16,
    marginBottom: 10,
  },
  content: { marginBottom: 10, fontSize: 15 },
  comment: {
    marginTop: 15,
    marginBottom: 3,
    opacity: 0.5,
  },
});

const buttons = StyleSheet.create({
  tags: {
    backgroundColor: "#ececec",
    borderRadius: 10,
    paddingHorizontal: 7,
    paddingVertical: 5,
    marginRight: 7,
    fontWeight: 500,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
  likes: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "white",
    borderWidth: 2,
    borderColor: "#ececec",
    borderRadius: 20,
    paddingHorizontal: 12,
    paddingVertical: 8,
    position: "absolute",
    top: -5,
  },
});
